class RealAPI{async request(endpoint){try{const response=await fetch(`${API_BASE}${endpoint}`,{headers:{'Content-Type':'application/json'}});if(!response.ok){throw new Error(`HTTP ${response.status}: ${response.statusText}`);}return response.json();}catch(error){console.error(`API Error for ${endpoint}:`,error);throw error;}}// Méthodes API simples
